INSERT INTO answer VALUES (6565, 1642, 0, 'Eine Amateurfunkstelle ist eine Funkstelle des Amateurfunkdienstes.', ' ');
INSERT INTO answer VALUES (6566, 1642, 1, 'Eine Amateurfunkstelle ist eine Funkstelle, die aus einer oder mehreren Sendefunkanlagen und Empfangsfunkanlagen einschließlich der zum Betrieb erforderlichen Zusatzeinrichtungen besteht.', ' ');
