INSERT INTO answer VALUES (6749, 1688, 0, 'Ein Funkamateur ist der Inhaber eines Amateurfunkzeugnisses oder einer harmonisierten Prüfungsbescheinigung, der sich mit dem Amateurfunkdienst aus persönlicher Neigung und nicht aus gewerblich-wirtschaftlichem Interesse befasst.', ' ');
INSERT INTO answer VALUES (6750, 1688, 1, 'Funkamateur ist jede natürliche Person, die Funkanlagen zu experimentellen und technisch-wissenschaftlichen Studien, zur eigenen Weiterbildung, aber nicht zu gewerblich-wirtschaftlichen Zwecken betreibt.', ' ');
INSERT INTO answer VALUES (6751, 1688, 2, 'Funkamateur ist jeder, der Amateurfunkgeräte besitzt und Amateurfunkaussendungen aus persönlicher Neigung empfängt.', ' ');
INSERT INTO answer VALUES (6752, 1688, 3, 'Im Sinne des AFuG sind Funkamateure nur die Inhaber einer Zulassung zum Amateurfunkdienst mit mindestens einem zugeteilten, personengebundenen Rufzeichen.', ' ');
INSERT INTO answer VALUES (6753, 1689, 0, 'lediglich aus persönlicher Neigung und nicht aus gewerblich-wirtschaftlichem Interesse mit dem Amateurfunkdienst befasst.', ' ');
