INSERT INTO answer VALUES (7471, 1868, 2, 'Bei Verwertung oder Weitergabe von Gesprächsinhalten und Daten aus Amateurfunkverbindungen, unabhängig davon, ob der Funkamateur selbst beteiligt war.', ' ');
