INSERT INTO answer VALUES (7520, 1880, 3, 'Die Betriebsfunkanlage ist so zu verändern, dass es zu keinen Störungen mehr kommt (z.B. Rauschsperre unempfindlicher einstellen, Veränderung des Antennenstandortes).', ' ');
INSERT INTO answer VALUES (7521, 1881, 0, 'Die Heizungssteuerung darf weiterbetrieben werden, wenn sie die für sie gültigen Grenzwerte aus den europäisch anerkannten Normen einhält.', ' ');
INSERT INTO answer VALUES (7522, 1881, 1, 'Die Heizungssteuerung ist außer Betrieb zu nehmen, da sie, unabhängig davon, auf welcher Frequenz es zu Störungen kommt, keine Aussendung in einem Amateurfunkband machen darf.', ' ');
INSERT INTO answer VALUES (7523, 1881, 2, 'Die Heizungssteuerung darf unabhängig von der Einhaltung irgendwelcher Grenzwerte unbeschränkt weiterbetrieben werden, da die gestörte Frequenz dem Amateurfunk nur auf sekundärer Basis zugewiesen ist.', ' ');
INSERT INTO answer VALUES (7524, 1881, 3, 'Die Heizungssteuerung darf aus Gründen der Verhältnismäßigkeit (Sekundärzuweisung) unabhängig von der Einhaltung irgendwelcher Grenzwerte innerhalb der Heizperioden weiterbetrieben werden.', ' ');
INSERT INTO answer VALUES (7525, 1882, 0, 'Die Heizungssteuerung darf weiterbetrieben werden, wenn sie die für sie gültigen Grenzwerte aus den europäisch anerkannten Normen einhält.', ' ');
INSERT INTO answer VALUES (7526, 1882, 1, 'Die Heizungssteuerung ist außer Betrieb zu nehmen, da sie keine Aussendung in einem Amateurfunkband machen darf.', ' ');
INSERT INTO answer VALUES (7527, 1882, 2, 'Die Heizungssteuerung muss umgehend außer Betrieb genommen werden, da sich die gestörte Frequenz in einem primär zugewiesenen Amateurfunkband befindet.', ' ');
INSERT INTO answer VALUES (7528, 1882, 3, 'Die Heizungssteuerung darf aus Gründen der Verhältnismäßigkeit unabhängig von der Einhaltung irgendwelcher Grenzwerte innerhalb der Heizperioden weiterbetrieben werden.', ' ');
INSERT INTO answer VALUES (7529, 1883, 0, 'Mit der Durchführung behördlicher Maßnahmen nach AFuV und EMVG (Überprüfung der Amateurfunkstelle und möglicherweise Betriebseinschränkungen).', ' ');
