INSERT INTO answer VALUES(13105, 3277, 1, 'Wenn eine Blitzschutz-Fachkraft die Verbindung des Standrohres der Amateurfunkantenne mit dem Blitzschutzsystem im Blitzschutzkonzept vorsieht.', '');
