INSERT INTO answer VALUES(13119, 3280, 3, 'Neben der Erdung des Antennenmastes sind keine weiteren Maßnahmen erforderlich.', '');
INSERT INTO answer VALUES(13120, 3280, 4, 'Die Koaxialkabel müssen ein Schirmungsmaß von mindestens 40 dB aufweisen.', '');
